clcclear allclose allNx = input('Enter the value of number of elements Nx=');Ny = input('Enter the value of number of elements Ny=');d = input('Enter the value of spacing(d)=');thetamax = input('Enter the value of theta in max direction=');phimax = input('Enter the value of phi in max direction=');[phi,theta]  = meshgrid(linspace(0,2*pi,300),linspace(0,pi,300));betax = -2*pi*d*cos(phimax)*sin(thetamax);betay = -2*pi*d*sin(phimax)*sin(thetamax);six = (2*pi*d.*cos(phi).*sin(theta))+betax;siy = (2*pi*d.*sin(phi).*sin(theta))+betay;AFx = (sin(Nx.*six./2)./(Ny.*sin(six./2)));AFy = (sin(Ny.*siy./2)./(Ny.*sin(siy./2)));AF = AFx.*AFy;[x,y,z] = sph2cart(theta,phi,AF);mesh(x,y,z);